import axios from "axios";

export const sendOrderToTelegram = async (orderData, items) => {
  try {
    const botToken = "7180030436:AAFrtXYz69R2ruFcTv2RXsJStlmAYrH8f1s";
    const chatId = "-1002015638471";
    const message = `Нове замовлення:     
    Імя: ${orderData.firstName}
    Email: ${orderData.Email} 
    Номер: ${orderData.number}
    Місто: ${orderData.city}
    Відділення: ${orderData.warehouse}`;
    const url = `https://api.telegram.org/bot${botToken}/sendMessage`;

    await axios.post(url, {
      chat_id: chatId,
      text: message,
    });
  } catch (error) {
    console.error("Error sending message to Telegram:", error);
  }
};
