import React, { useContext, useEffect, useState } from "react";
import { Context } from "../index";
import { CategoryCard } from "../components/CategoryCard";
import { observer } from "mobx-react-lite";
import styles from "../styles/CategoryPage.module.css";
import CategoryPagination from "../components/CategoryPagination";
import { fetchCategories } from "../api/ItemApi";

const CategoryPage = () => {
  const { item } = useContext(Context);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Data fetching (replace with your actual API call)
  useEffect(() => {
    fetch("https://your-api-endpoint.com/categories") // Replace with your API endpoint
      .then((response) => response.json())
      .then((data) => {
        context.updateState({ item: { ...item, category: data } }); // Update context
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        console.error("Error fetching data:", error);
      });
  }, []); // Empty dependency array to fetch data only once

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }
  
  return (
    <>
      <div className={styles.gridWrapper}>
        <div className={styles.grid}>
          {item.category.map((element) => (
            <CategoryCard key={element._id} element={element} />
          ))}
        </div>
        <CategoryPagination />
      </div>
    </>
  );
};

export default observer(CategoryPage);
