import { useParams } from 'react-router-dom'
import { LoadingScreen } from '../../components/micro/loadingScreen/loading.js'
import { fetchProductByCategory } from '../../api/ItemApi.js'
import { Content } from 'antd/es/layout/layout.js'

export const ItemsByCategory = () => {
  const { category } = useParams()
  const [loading, setLoading] = useState(true)
  const [items, setItems] = useState([])
  const { item } = useContext(Content)
  
  useEffect(() => {
    const fetchData = () => {
      fetchProductByCategory(category).then((data) => setItems(data))
    }
    fetchData()
    setLoading(false)
  }, [category, items])
