import React, { useEffect, useState } from 'react'
import styles from './CreateProduct.module.css'
import { getCategories } from '../../../api/ItemApi'
import { Select } from 'antd'

export const CreateProduct = ({ show, onHide }) => {
  const [name, setName] = useState()
  const [file, setFile] = useState()
  const [categories, setCategories] = useState()
  const [selectedCategory, setSelectedCategory] = useState()
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const fetchData = () => {
      getCategories().then((data) => setCategories(data))
    }
    fetchData()
    setLoading(false)
  }, [])

  const addProduct = () => {
    const formData = new FormData()
    formData.append('img', file)
  }

  const selectFile = (e) => {
    setFile(e.target.files[0])
  }

  return (
    <div
      className={styles.ModalBackground}
      style={show ? { display: 'flex' } : { display: 'none' }}
    >
      <div className={styles.modalContainer}>
        <div className={styles.modalHedaer}>
          <span>Додати товар</span>
        </div>
        <div className={styles.ModalInput}>
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder={'Введіть назву товара'}
          />
          <Select
            mode="single"
            value={categories}
            placeholder="Виберіть місто"
            onChange={(value) => setSelectedCategory(value)}
            showSearch={true}
            style={{ width: 300 }}
            filterOption={false}
          >
            {!loading && categories.map((item) => (
              <Select.Option key={item._id} value={item.name}>
                {item.name}
              </Select.Option>
            ))}
          </Select>
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder={'Введите ціну товара'}
          />
        </div>
        <div className={styles.ModalFileInput}>
          <input onChange={selectFile} type="file" />
        </div>
        <div className={styles.ModalButtonsWrapper}>
          <button className={styles.ModalButtonReject} onClick={onHide}>
            Закрыть
          </button>
          <button className={styles.ModalButtonAccept} onClick={addProduct}>
            Добавить
          </button>
        </div>
      </div>
    </div>
  )
}
