import React, { useEffect, useState } from "react";
import styles from "../styles/BasketItem.module.css";
import { Counter } from "./micro/Counter";
import { DeleteIcon } from "./micro/Arrows";
import { fetchOneProduct } from "../api/ItemApi";

export const BasketItem = ({ itemId, count }) => {
  const [countState, setCountState] = useState(count);
  const [product, setProduct] = useState();
  const [loading, setLoading] = useState(true);
  const [total, setTotal] = useState();
  console.log(itemId.itemId);
  // useEffect(() => {
  //   fetchOneProduct(itemId).then((data) => console.log(itemId));
  // });

  if (loading) {
    return <div></div>;
  }
  if (!loading)
    return (
      <div className={styles.BasketItemWrapper}>
        <div className={styles.nameWrapper}>
          <img
            style={{ width: 80, height: 80 }}
            src={process.env.REACT_APP_API_URL + item.img[0]}
          />
          <p>{item.name}</p>
        </div>
        <div className={styles.PriceWrapper}>
          <p>{item.price} грн.</p>
        </div>
        <div className={styles.countWrapper}>
          <Counter
            count={countState}
            addCount={() => {
              setCountState(countState + 1);
              setTotal(item.price * (countState + 1));
            }}
            minusCount={() => {
              if (countState > 1) {
                setCountState((countState) => countState - 1);
                setTotal(item.price * (countState - 1));
              } else {
                setCountState(1);
                setTotal(item.price);
              }
            }}
          />
          <div className={styles.deleteIconWrapper}>
            <DeleteIcon />
          </div>
        </div>
        <div className={styles.totalWrapper}>{total} грн.</div>
      </div>
    );
};
