import React, { useContext, useEffect, useState } from "react";
import { Context } from "../index";
import { fetchCategories } from "../api/ItemApi";
import { CategoryCard } from "../components/CategoryCard";
import { observer } from "mobx-react-lite";
import styles from "../styles/CategoryPage.module.css";
import Pages from "../components/Pages";
import { Pagination } from "react-bootstrap";

const CategoryPage = () => {
  const { item } = useContext(Context);
  const [totalCount, setTotalCount] = useState();
  const pages = [];
  useEffect(() => {
    fetchCategories(1, 18).then((data) => {
      item.setCategory(data.category);
      setTotalCount(data.totalCount);
    });
  }, [item.category]);
  const pageCount = Math.ceil(totalCount / 18);
  for (let i = 0; i < pageCount; i++) {
    pages.push(i + 1);
  }

  return (
    <>
      <div className={styles.gridWrapper}>
        <div className={styles.grid}>
          {item.category.map((element) => (
            <CategoryCard key={element._id} element={element} />
          ))}
        </div>
      </div>
      <div className="mt-3">
        {pages.map((page) => (
          <Pagination.Item
            key={page}
            active={item.page === page}
            onClick={() => item.setPage(page)}
          >
            {page}
          </Pagination.Item>
        ))}
      </div>
    </>
  );
};

export default observer(CategoryPage);
