import React from "react";
import {
  ButtonBack,
  ButtonFirst,
  ButtonLast,
  ButtonNext,
  CarouselProvider,
  DotGroup,
  ImageWithZoom,
  Slide,
  Slider,
} from "pure-react-carousel";
import "pure-react-carousel/dist/react-carousel.es.css";
import styles from "../styles/Carousel.module.css";

export const ImgSlider = ({ images }) => {
  return (
    <>
      <CarouselProvider
        style={{ margin: 100 }}
        visibleSlides={1}
        totalSlides={8}
        step={1}
        naturalSlideWidth={400}
        naturalSlideHeight={500}
        hasMasterSpinner
        infinite
      >
        <DotGroup dotNumbers />
        <Slider style={{ width: 400 }}>
          <Slide index={0}>
            <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} />
          </Slide>
          <Slide index={1}>
            <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} />
          </Slide>
          <Slide index={2}>
            <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} />
          </Slide>
          <Slide index={3}>
            <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} />
          </Slide>
          <Slide index={4}>
            <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} />
          </Slide>
          <Slide index={5}>
            <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} />
          </Slide>
          <Slide index={6}>
            <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} />
          </Slide>
          <Slide index={7}>
            <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} />
          </Slide>
        </Slider>
        <ButtonFirst>First</ButtonFirst>
        <ButtonBack>Back</ButtonBack>
        <ButtonNext>Next</ButtonNext>
        <ButtonLast>Last</ButtonLast>
      </CarouselProvider>
      {/* <ImageWithZoom src={process.env.REACT_APP_API_URL + images[0]} /> */}

      {/* {images.map((img) => (
        <img src={process.env.REACT_APP_API_URL + img} />
      ))} */}
    </>
  );
};
