import React from "react";

export const CartTotal = () => {
  useEffect(() => {
    setInterval(() => {
      if (userStore.isAuth) {
        getBasket(userStore.user.id).then((data) => {
          setItems(data.basket.basketItem);
          setLoading(false);
        });
      }
    }, 500);
  }, [userStore.isAuth]);
  return <div>CartTotal</div>;
};
