import React, { useContext, useEffect } from "react";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { Row } from "react-bootstrap";
import ProductCard from "./ProductCard";
import { fetchProduct } from "../api/ItemApi";
import styles from "../styles/ItemList.module.css";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const DeviceList = observer(() => {
  const { item } = useContext(Context);
  useEffect(() => {
    // fetchTypes().then((data) => item.setTypes(data));
    // fetchBrands().then((data) => item.setBrands(data));
    fetchProduct(1, 6).then((data) => {
      item.setItems(data.items);
      item.setTotalCount(data.totalCount);
    });
  }, []);

  useEffect(() => {
    fetchProduct(item.page, 6).then((data) => {
      item.setItems(data.items);
      item.setTotalCount(data.totalCount);
    });
  }, [item.page]);

  function SampleNextArrow(props) {
    return (
      <div
        className={className}
        style={{ ...style, display: "block", background: "red" }}
        onClick={onClick}
      />
    );
  }

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
    nextArrow: <SampleNextArrow />,
  };
  return (
    <div className={styles.ItemListContainer}>
      <div className={styles.ItemSliderContainer}>
        <Slider {...settings} styles={{ background: "black" }}>
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
          <div>
            <h3>7</h3>
          </div>
          <div>
            <h3>8</h3>
          </div>
          <div>
            <h3>9</h3>
          </div>
        </Slider>
      </div>
    </div>
  );
});

export default DeviceList;
