import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { LoadingScreen } from '../../components/micro/loadingScreen/loading.js'
import { fetchProductByCategory } from '../../api/ItemApi.js'

export const ItemsByCategory = () => {
  const { category } = useParams()
  const [loading, setLoading] = useState(true)
  const [items, setItems] = useState([])

  useEffect(() => {
    const fetchData = () => {
      fetchProductByCategory(category).then((data) => setItems(data))
    }
    fetchData()
    setLoading(false)
  }, [category, items])

  if (loading) {
    return <LoadingScreen />
  }

  return <div>ItemsByCategory</div>
}
