import React, { useContext, useEffect, useState } from "react";
import { Context } from "../index";
import { getBasket } from "../api/BasketApi";
import { BasketItem } from "./BasketItem";
import style from "../styles/Basket.module.css";
import { fetchOneProduct } from "../api/ItemApi";

export const Basket = () => {
  const { userStore } = useContext(Context);
  const [items, setItems] = useState([]); // Set initial value to an empty array
  const [loading, setLoading] = useState(true);
  const [total, setTotal] = useState(0);

  // useEffect(() => {
  //   getBasket(userStore.user.id).then((data) => {
  //     const fetchData = async () => {
  //       const data = await getBasket(userStore.user.id);
  //       const productPromises = data.basket.basketItem.map((element) =>
  //         fetchOneProduct(element.itemId)
  //       );
  //       const resolvedProducts = await Promise.all(productPromises);
  //       setItems(resolvedProducts);
  //       setLoading(false);
  //     };
  //     fetchData();
  //   });
  // }, [userStore.isAuth]);

  // console.log(items);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (userStore.isAuth && items.length > 0) {
    // Check if user is authenticated and items exist
    return (
      <>
        <div className={style.BasketHeader}>
          <h4>Продукт</h4>
          <h4>Ціна</h4>
          <h4>Кількість</h4>
          <h4 id={style.totalText}>Всього</h4>
        </div>
        {items.map((element) => (
          <BasketItem
            key={element._id}
            itemId={element}
            count={element.count}
          />
        ))}
        <h4>Всього: {total}</h4>
      </>
    );
  }

  return null; // Return null when not authenticated or items haven't loaded
};
