import React, { useContext, useEffect, useState } from "react";
import { Context } from "../";

const LoginForm = () => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const { userStore } = useContext(Context);

  useEffect(() => {
    const registerBtn = document.getElementById('register')
    const container = document.getElementById('container')
    const loginBtn = document.getElementById('login')
    registerBtn.addEventListener('click', () => {
      container.classList.add("active")
    })
    loginBtn.addEventListener('click', () => {
      container.classList.remove("active")
    })
  }, [])


  return (
    <>
      <div class="container" id="container">
        <div class="form-container sign-up">
          <form>
            <h1>Create Account</h1>

            <input type="text" placeholder="Name" />
            <input
              onChange={(e) => setEmail(e.target.value)}
              value={email}
              type="email"
              placeholder="Email"
            />
            <input
              onChange={(e) => setPassword(e.target.value)}
              value={password}
              type="password"
              placeholder="Password"
            />
            <button onClick={() => userStore.loginBtn(email, password)}>Логин</button>
          </form>
        </div>
        <div class="form-container sign-in">
          <form>
            <h1>Sign In</h1>
            <input type="email" placeholder="Email" />
            <input type="password" placeholder="Password" />
            <a href="#">Forget your password?</a>
            <button>Sign In</button>
          </form>
        </div>
        <div class="toogle-container">
          <div class="toogle">
            <div class="toogle-pannel toogle-left">
              <h1>Welcome Back!</h1>
              <p>Enter your personal details to use all of site features</p>
              <button class="hidden" id="login">Sign In</button>
            </div>
            <div class="toogle-pannel toogle-right">
              <h1>Hello!</h1>
              <p>Register with your personal details to use all of site features</p>
              <button class="hidden" id="register" onClick={() => userStore.registration(email, password)}>
                Регистрация
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default LoginForm;
