import React, { useContext, useEffect } from "react";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { Row } from "react-bootstrap";
import ProductCard from "./ProductCard";
import { fetchProduct } from "../api/ItemApi";
import styles from "../styles/ItemList.module.css";
import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const DeviceList = observer(() => {
  const { item } = useContext(Context);
  useEffect(() => {
    // fetchTypes().then((data) => item.setTypes(data));
    // fetchBrands().then((data) => item.setBrands(data));
    fetchProduct(1, 6).then((data) => {
      item.setItems(data.items);
      item.setTotalCount(data.totalCount);
    });
  }, []);

  useEffect(() => {
    fetchProduct(item.page, 6).then((data) => {
      item.setItems(data.items);
      item.setTotalCount(data.totalCount);
    });
  }, [item.page]);

  function SampleNextArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{ ...style, display: "block", color: "black" }}
        onClick={onClick}
      />
    );
  }

  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    nextArrow: (
      <div>
        <div className="next-slick-arrow">
          <svg
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M3.5 12H20M20 12L13 5M20 12L13 19"
              stroke="black"
              stroke-width="1.5"
              stroke-linecap="round"
              stroke-linejoin="round"
            />
          </svg>
        </div>
      </div>
    ),
    prevArrow: (
      <div className={styles.PrevArrow}>
        <div className="next-slick-arrow" style={{ marginTop: 5 }}>
          <svg
            width="18"
            height="16"
            viewBox="0 0 18 16"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M8 1L1 8L8 15M1 8H17"
              stroke="black"
              stroke-width="1.5"
              stroke-linecap="round"
              stroke-linejoin="round"
            />
          </svg>
        </div>
      </div>
    ),
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };
  return (
    <div className={styles.ItemListContainer}>
      <div className={styles.ItemSliderContainer}>
        <Slider {...settings}>
          {item.items.map((item) => (
            <ProductCard key={item._id} item={item} />
          ))}
        </Slider>
      </div>
    </div>
  );
});

export default DeviceList;
