import React from 'react'

export const NovaPoshta = () => {
  return (
    <>
      <Select
        mode="single"
        value={selectedCity}
        placeholder="Виберіть місто"
        showSearch={true}
        onChange={(value) => {
          setSelectedCity(value);
          getWarehouse(value);
        }}
        onSearch={(value) => setSearchCityString(value)} // Обновляем значение строки поиска при изменении
        className={style.BilingSelectWarehouse}
        loading={loading}
        filterOption={false} // Отключаем автоматический фильтр опций
        notFoundContent={loading ? "Loading..." : "No data found"} // Отображаем сообщение при отсутствии данных
      >
        {regions.map((item) => (
          <Select.Option key={item.Ref} value={item.Description}>
            {item.Description}
          </Select.Option>
        ))}
      </Select>
      <Select
        mode="single"
        placeholder="Виберіть відділення"
        value={selectedWarehouses}
        showSearch={true}
        onChange={(value) => {
          setSelectedWarehouses(value);
          getWarehouse(value);
        }}
        style={{ width: 400 }}
        loading={loading}
        filterOption={false} // Отключаем автоматический фильтр опций
        notFoundContent={loading ? "Loading..." : "No data found"} // Отображаем сообщение при отсутствии данных
      >
        {warehouses.map((item) => (
          <Select.Option
            style={{ fontSize: "12px" }}
            key={item.Ref}
            value={item.Description}
          >
            {item.Description}
          </Select.Option>
        ))}
      </Select>
    </>
  );
}
