import React, { useContext, useEffect } from "react";
import styles from "../styles/CategoryList.module.css";
import { Context } from "../index";
import { fetchCategories } from "../api/ItemApi";

export const CategoryList = () => {
  const { item } = useContext(Context);
  console.log(item);

  useEffect(() => {
    fetchCategories().then((data) => {
      item.setCategory(data);
    });
  }, [item.category]);
  console.log();
  return (
    <div className={styles.CategoryContainer}>
      <ul style={{ listStyle: "none" }}>
        <li className={styles.CategoryListMainItem}>All Category</li>
        <li className={styles.CategoryListItem}>Category</li>
        {item.category.map((element) => (
          <li className={styles.CategoryListItem} key={element._id}>
            {element.name}
          </li>
        ))}
      </ul>
    </div>
  );
};
