import React, { useEffect, useState } from "react";
import style from "../styles/BilingDetails.module.css";
import { NewPostGetCity, NewPostGetRegion } from "../api/NewPostApi";
import { Select, Button } from "antd";
export const BilingDetails = () => {
  const [regions, setRegions] = useState();
  const [cities, setCities] = useState();
  const [selectedRegion, setSelectedRegion] = useState();
  const [selectedCity, setSelectedCity] = useState();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    NewPostGetRegion().then((data) => setRegions(data));
    setTimeout(() => {
      setLoading(false);
    }, 500);
  });

  const getCity = (ref) => {
    return NewPostGetCity(ref).then((data) => setCities(data));
  };

  if (loading) {
    return <div></div>;
  }

  return (
    <div className={style.BilingWrapper}>
      <div className={style.BilingContainer}>
        <div className={style.BilingDetailsContainer}>
          <p>Деталі замовлення</p>
          <div>
            <div>
              <label>Ваше імя</label>
              <input />
            </div>
            <div>
              <label>Ваше прізвище</label>
              <input />
            </div>
            <div>
              <label>Ваше місто</label>
              <input />
            </div>
            <div>
              <label>Ваш номер телефону</label>
              <input />
            </div>
          </div>
        </div>
        <div className={style.BilingSuccessContainer}>
          <Select mode="multiple" style={{ width: 120 }}>
            <Select.Option value="jack">Jack</Select.Option>
            <Select.Option value="lucy">Lucy</Select.Option>
            <Select.Option value="disabled" disabled>
              Disabled
            </Select.Option>
            <Select.Option value="Yiminghe">yiminghe</Select.Option>
          </Select>
          {/* {regions.map((item) => (
              <option key={item.id} value={item.Ref}>
                {item.Description} */}
        </div>
      </div>
    </div>
  );
};
