import React, { useEffect, useState } from 'react'
import styles from './CreateProduct.module.css'
import { createProduct, getCategories } from '../../../api/ItemApi'
import { Select } from 'antd'

export const CreateProduct = ({ show, onHide }) => {
  const [name, setName] = useState()
  const [file, setFile] = useState()
  const [categories, setCategories] = useState()
  const [selectedCategory, setSelectedCategory] = useState()
  const [loading, setLoading] = useState(true)
  const [price, setPrice] = useState()

  useEffect(() => {
    const fetchData = () => {
      getCategories().then((data) => setCategories(data))
      setTimeout(() => {
        setLoading(false)
      }, 500)
    }
    fetchData()
  }, [])

  const addProduct = () => {
    const formData = new FormData()
    formData.append('img', file)
    formData.append('category', selectedCategory)
    formData.append('name', name)
    formData.append('price', price)
    createProduct(formData)
    onHide()
  }

  const selectFile = (e) => {
    setFile(e.target.files[0])
  }

  if (loading) {
    return <>loading</>
  }

  return (
    <div
      className={styles.ModalBackground}
      style={show ? { display: 'flex' } : { display: 'none' }}
    >
      <div className={styles.modalContainer}>
        <div className={styles.modalHedaer}>
          <span>Додати товар</span>
        </div>
        <div className={styles.ModalInput}>
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder={'Введіть назву товара'}
          />
          {loading ? (
            <span>loading</span>
          ) : (
            <Select
              mode="single"
              value={selectedCategory}
              placeholder="Виберіть категорію"
              onChange={(value) => setSelectedCategory(value)}
              showSearch={true}
              style={{ width: 240, marginLeft: 25 }}
              filterOption={false}
            >
              {categories.map((item) => (
                <Select.Option key={item._id} value={item.name}>
                  {item.name}
                </Select.Option>
              ))}
            </Select>
          )}
          <input
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder={'Введите ціну товара'}
          />
        </div>
        <div className={styles.ModalFileInput}>
          <input onChange={selectFile} type="file" />
        </div>
        <div className={styles.ModalButtonsWrapper}>
          <button className={styles.ModalButtonReject} onClick={onHide}>
            Закрыть
          </button>
          <button className={styles.ModalButtonAccept} onClick={addProduct}>
            Добавить
          </button>
        </div>
      </div>
    </div>
  )
}
